CHAR_SIZE = len(chars) + 2 # 1 for unknown and 1 for starting from 1
char_id_map = dict([(char, i) for i, char in enumerate(chars)])

def char_to_idx(char):
    if char in char_id_map:
        return char_id_map[char] + 1
    return CHAR_SIZE - 1

def line_to_char_ids(line):
    return [char_to_idx(x) for x in list(line.strip())]

if __name__ == '__main__':
    print(CHAR_SIZE)
